mapper.select:
select v.*, (select concat(a.itvday, ' ', a.itvtime) from ck_interview_info a where a.idx = v.hopeintv1 and a.recruitidx = #{catenum} limit 1) as interviewday1, (select concat(a.itvday, ' ', a.itvtime) from ck_interview_info a where a.idx = v.hopeintv2 and a.recruitidx = #{catenum} limit 1) as interviewday2, concat(v.birthyear, '. ', v.birthmonth, '. ', v.birthday) as birth, concat(v.phoneagency, '-', v.phonefirst, '-', v.phonesecond) as phonenum, s.status as studentcnt, s.idx as studentidx from ck_recruit_volunteer v left outer join ck_student_info s on s.mobile = concat(v.phoneagency, '-', v.phonefirst, '-', v.phonesecond) and s.korname = v.korname where v.catenum = #{catenum}
and v.hopeintv1 = #{hopeidx}
and v.hopeintv2 = #{hopeidx}
and v.result1st = '합격'
and v.result2nd = '합격'
and v.result3rd = '합격'
order by v.regdate desc

select *, (select concat(a.itvday, ' ', a.itvtime) from ck_interview_info a where a.idx = hopeintv1 and a.recruitidx = #{catenum} limit 1) as interviewday1, (select concat(a.itvday, ' ', a.itvtime) from ck_interview_info a where a.idx = hopeintv2 and a.recruitidx = #{catenum} limit 1) as interviewday2, concat(birthyear, '. ', birthmonth, '. ', birthday) as birth, concat(phoneagency, '-', phonefirst, '-', phonesecond) as phonenum, ifnull((select status from ck_student_info s where s.mobile = concat(phoneagency, '-', phonefirst, '-', phonesecond) and s.korname = korname),'') as studentcnt from ck_recruit_volunteer where catenum = #{catenum}
and hopeintv1 = #{hopeidx}
and hopeintv2 = #{hopeidx}
and result1st = '합격'
and result2nd = '합격'
and result3rd = '합격'
order by catenum, studentnum asc

select 
	count(a.idx) as count
from ck_recruit_volunteer a
where a.catenum = #{catenum} 
and a.korname = #{korname}
and concat(a.phoneagency, a.phonefirst, a.phonesecond) = concat(#{phoneagency}, #{phonefirst}, #{phonesecond})

mapper.update:
        update ck_recruit_volunteer
			set
			result1st = #{value}
		where idx = #{idx};

        update ck_recruit_volunteer
			set
			result2nd = #{value}
		where idx = #{idx};

        update ck_recruit_volunteer
			set
			result3rd = #{value}
		where idx = #{idx};

        update ck_recruit_volunteer set
			hopeintv1 = #{ hopeintv1 }
		where idx = #{idx};

        update ck_recruit_volunteer set
			hopeintv2 = #{ hopeintv2 }
		where idx = #{idx};
