mapper.select:
SELECT a.*, (select b.note from ck_consult_note_info b where b.consultidx = a.idx order by b.idx desc limit 1) as note, (select b.regdate from ck_consult_note_info b where b.consultidx = a.idx order by b.idx desc limit 1) as updatedate FROM ck_consult_info a WHERE 1 = 1
AND a.cons_status = #{cons_status}
AND STR_TO_DATE(a.cons_day, '%Y-%m-%d') >= STR_TO_DATE(#{cons_startday}, '%Y-%m-%d')
AND STR_TO_DATE(a.cons_day, '%Y-%m-%d') <= STR_TO_DATE(#{cons_endday}, '%Y-%m-%d')
AND a.cons_manager = #{cons_manager}
ORDER BY a.cons_status asc, a.cons_day DESC, a.cons_time DESC

mapper.insert:
INSERT INTO ck_consult_note_info
	(idx, consultidx, consultcode, note, regid, regdate)
VALUES ((select ifnull(max(idx)+1, 1) from ck_consult_note_info a), #{consultidx}, #{consultcode}, #{note}, #{regid}, now());
